import { useState, useEffect } from "react";
import "./chatStyles.css";
import { io } from "socket.io-client";

export default function() {
    const [msg, setMsg] = useState([{
        class: "otherUser",
        message: "Everybody's got opinions on our thing Say we're flying down a path with no ending And if I die before I wake Ooh, don't let me wake up from this dream"
    },
    {
        class: "ogUser",
        message: "When we collide When we collide, it's a beautiful disaster When I crash into you, you, you Crash into you, you"
    }]);

    function showMessages(item) {
        return (
            <div className={`msg ${item.class}`}>
                {item.message}
            </div>
        )
    }

    useEffect(()=> {
        const ENDPOINT = "http://localhost:4000";
    
        const socket = io(ENDPOINT, { secure: true });
        socket.on("connect", function(){
            console.log("connected");
        });
    
        socket.on("disconnect", function(){
            console.log("disconnected");
        });
    
        socket.on("welcome", (message) => {
          console.log(message);
          console.log("D");
        });
    
        socket.on("another", (message) => {
          console.log(message);
        })
    
        socket.on("u_disconnect", (message) => {
          console.log(message);
        });
      },[]);

    function sendMessage(e) {
        e.preventDefault();
        console.log(e.target.input.value);
    }

    return (
        <>
            <section className="chatContainer">
                <div className="banner"></div>
                <div id="messages">
                    {msg.map(showMessages)}
                </div>
                <div className="messageBox">
                    <form id="form" action="" onSubmit={sendMessage}>
                    <input id="input" autoComplete="off" /><button>Send</button>
                    </form>
                </div>
            </section>
        </>
    )
}
