import { useLocation } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";

import Navbar from "./navbar";

export default function() {
    // let location = useLocation();
    // console.log(location);
    const transVariants = {
        from: { scale: 0.6, opacity: 0 },
        in: { scale: 1, opacity: 1, transition: {duration: 1}},
        out: {
          scale: 0.3,
          opacity: 0,
          transition: { duration: 2 , when: "afterChildren"}
        }
    };

    let title = "motto motto as beautiful as the moon motto motto";
    title = title.split(' ');

    function titleAnim(item,ind) {
        const titleVariants = {
            initial: {y: "101%"},
            animate: i=>({y: "0", transition: {duration: 0.5, delay: 2.1 + i*0.13, type: "tween"}})
        }
        return (
        <motion.div variants={titleVariants} initial="initial" animate="animate" custom={ind} style={{display: "inline-block"}}>
            {item + (ind === title.length-1 ? "" : "\u00A0")}
        </motion.div>)
    }

    const cirVar = {
        initial: {opacity: 0,scale: 0},
        enter: {opacity: 1,scale: 15, transition: {delay: 1, duration: 0.6, type: "tween"}}
    }

    const oneLinerVar = {
        initial: {translateY: "30%", opacity: 0},
        enter: {translateY: "0%",opacity: 1, transition: {delay: 1.6, duration: 0.5, type: "tween"}}
    }

    const abVar = {
        initial: {translateX: "-35%", opacity: 0},
        enter: {translateX: "0%",opacity: 1, transition: {delay: 3.3, duration: 1, type: "spring"}}    
    }

    const contVar = {
        initial: {translateX: "-20%", opacity: 0},
        enter: {translateX: "0%",opacity: 1, transition: {delay: 3.6, duration: 0.6, type: "tween"}}    
    }

    return(
        <>
        <section className="l-sec" id="thirdSection">
            <Navbar />
            <motion.svg variants={cirVar} initial="initial" animate="enter" height="100" width="100">
                <circle cx="50" cy="0" r="40" stroke="black" stroke-width="3" fill="red" />
                Sorry, your browser does not support inline SVG.  
            </motion.svg>
            <div className="container-fluid">
                <motion.h1 variants = {oneLinerVar} initial="initial" animate="enter" className="oneLiner">One Liner</motion.h1>
                <h3 className="motto">{title.map(titleAnim)}</h3>
                <div className="desc">
                    <motion.h3 variants={abVar} initial="initial" animate="enter">About <span className="amor">amor</span></motion.h3>
                    <motion.p variants={contVar} initial="initial" animate="enter">amor is an online social dating site. amor is an online social dating site. 
