import io from "socket.io-client";

const socketReducer = (state = null, action) => {
    const newSocket = io(
        'http://localhost:4000',
        { query: { id } }
      )
      newSocket.emit("hi","bitch");

    switch (action.type) {
      default:
        return newSocket;
    }
  };
  
  export default socketReducer;
