import { useLocation } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";

import Navbar from "./navbar";

export default function() {
    // let location = useLocation();
    // console.log(location);
    const transVariants = {
        from: { scale: 0.6, opacity: 0 },
        in: { scale: 1, opacity: 1, transition: {duration: 1}},
        out: {
          scale: 0.3,
          opacity: 0,
          transition: { duration: 2 , when: "afterChildren"}
        }
    };
    return(
        <>
        <section className="l-sec" id="firstSection">
            <div className="aboutTrans1"></div>
            <div className="aboutTrans2"></div>
            <Navbar />
            <div className="textcenter" style={{overflow: "hidden"}}>
                <h1 style = {{fontSize: "2rem"}}>we all need love to raw dog our hearts</h1>
            </div>
            <div id="firstImg">
                <img src={process.env.PUBLIC_URL + "/images/firstSec.jpg"} />
                <div className="imgOverlay"></div>
            </div>
        </section>
        </>
    )
}
