import "./landingStyles.css";
import React from "react";
import { BrowserRouter, Route, Switch, useLocation } from "react-router-dom";

import About from "../components/landingComp/about";
import FirstSection from "../components/landingComp/firstSection";
import SecondSection from "../components/landingComp/secondSection";
import SignUpPage from "./signUpPage";
import { useTransition, animated } from 'react-spring'

import { AnimatePresence } from "framer-motion";

function Landing() {
  const location = useLocation()
  const transitions = useTransition(location, (location) => location.pathname, {
    config: { delay: 3000, duration: 2000 },
    from: { opacity: 0, transform: 'translate3d(100%,0,0)', x: -100 },
    enter: { opacity: 1, transform: 'translate3d(0%,0,0)', x: 0 },
    leave: { opacity: 1, transform: 'translate3d(0%,0,0)', x: 100, delay: 1000 },
  })  

  
  return (
    <BrowserRouter>
        <Route
        render={({ location }) => (
          <AnimatePresence exitBeforeEnter>
            <Switch location={location} key={location.key}>
              <Route exact path='/' render={() => <FirstSection />} />
              <Route path='/about' render={() => <About />} />
              <Route path='/login' render={() => <SecondSection />} />
              <Route path="/signup" render={() => <SignUpPage />} />
            </Switch>
          </AnimatePresence>
        )}
      />
      </BrowserRouter>
  );
}

export default Landing;
