import { useState } from "react";
import { useSelector } from "react-redux";
import { AnimatePresence, motion } from "framer-motion";
import Page0 from "./page0";
import Page1 from "./page1";

export default function () {
  const [step, setStep] = useState(0);

  const arr = [Page0, Page1];
  const [data, setData] = useState({
    f_name: "",
    l_name: "",
    email: "",
    password: "",
    age: "",
    sex: ""
  });

  function next() {
    setStep(step + 1);
  }

  function prev() {
    setStep(step - 1);
  }

  function sub() {
    //Send data to database
  }

  function onChange(e) {
    setData({ ...data, [e.target.name]: e.target.value });
  }

  function content() {
    switch (step) {
      case 0:
        return <Page0 next={next} data={data} change={onChange} />;

      case 1:
        return <Page1 prev={prev} data={data} sub={sub} change={onChange} />;

      default:
        return <Page0 next={next} data={data} change={onChange} />;
    }
  }

  const formVar = {
    initial: {translateX: "-60%", opacity: 0},
    enter: {translateX: "0%", opacity:1, transition: {
      ease: [0.43, 0.13, 0.23, 0.96,0.99],
      duration: 1
    }},
    exit: {translateX: "40%", opacity:0, transition: {
      ease: [0.43, 0.13, 0.23, 0.96],
      duration: 1
    }}
  }

  return (
    <div className="formContainer">
      <motion.div variants={formVar} initial="initial" animate="enter" exit="exit" className="Container">
        <div id="signUpForm">{content()}</div>
      </motion.div>
    </div>
  );
}
